            btn1: estadoAnterio.btn1 +1 
        }))

    }// fim da função mudaEstado
    mudaEstado2 = () => {        
        this.setState((estadoAnterio, _props)=>({
            btn2: estadoAnterio.btn2 +1 
        }))

    }// fim da função mudaEstado
    mudaEstado3 = () => {        
        this.setState((estadoAnterio, _props)=>({
            btn3: estadoAnterio.btn3 +1 
        }))

    }// fim da função mudaEstado
    render () {
      return (
        <>
          <button onClick={this.mudaEstado1}>Numero de Clicks {this.state.btn1}</button>
          <button onClick={this.mudaEstado2}>Numero de Clicks {this.state.btn2}</button>
          <button onClick={this.mudaEstado3}>Numero de Clicks {this.state.btn3}</button>
        </>
      )
    } // fim do render
} // fim EstEvent
