import React from 'react';
/**
 * Agora você vai converter sua aplicação para uma que conta o número de cliques dado em cada botão! Primeiramente, defina um estado inicial para a contagem de cada botão.
 */
class EstEvent3 extends React.Component {
    constructor () {
        super ()

        this.mudaEstado1 = this.mudaEstado1.bind(this);
        this.mudaEstado2 = this.mudaEstado2.bind(this);
        this.mudaEstado3 = this.mudaEstado3.bind(this);

        this.state = {
            btn1: 0,
            btn2: 0,
            btn3: 0,

        } // fim do obj
    }// fim do constructor
    mudaEstado1 = () => {        
       alert(clicou)

    }// fim da função mudaEstado
    mudaEstado2 = () => {        
        this.setState((est2)=>({
            btn2: est2.btn2 +1 
        }))

    }// fim da função mudaEstado
    mudaEstado3 = () => {        
        this.setState((est3)=>({
            btn3: est3.btn3 +1 
        }))

    }// fim da função mudaEstado
    render () {
      return (
        <>
          <button onClick={this.mudaEstado1}>Botão 1 | N-click {this.state.btn1} </button>
        </>
      )
    } // fim do render
} // fim EstEvent
export default EstEvent3;
