import React from 'react';
/**
 * Agora você vai converter sua aplicação para uma que conta o número de cliques dado em cada botão! Primeiramente, defina um estado inicial para a contagem de cada botão.
 */
class EstEvent3 extends React.Component {
    constructor () {
        super ()

        this.mudaEstado = this.mudaEstado.bind(this);


        this.state = {
            numeroDeCliques: 0
        } // fim do obj
    }// fim do constructor
    mudaEstado = () => {
        /* Passando uma callback à função setState, que recebe de parâmetros
    o estado anterior e as props do componente, você garante que as atualizações
    do estado acontecerão uma depois da outra! */
        this.setState((estadoAnterio, _props)=>({
            numeroDeCliques: estadoAnterio.numeroDeCliques +1 
        }))

    }// fim da função mudaEstado
    mudaEstado2 = () => {        
        this.setState((est2)=>({
            btn2: est2.btn2 +1 
        }))

    }// fim da função mudaEstado
    mudaEstado3 = () => {        
        this.setState((est3)=>({
            btn3: est3.btn3 +1 
        }))

    }// fim da função mudaEstado
    render () {
      return (
        <>
        <button onClick={this.mudaEstado}>Numero de Clicks {this.state.numeroDeCliques}</button>
        </>
      )
    } // fim do render
} // fim EstEvent
export default EstEvent3;
